import { Component } from '@angular/core';

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css'],
})
export class NavBarComponent {
  modeSwitch(): void {
    const navBarText = document.getElementById('LogoText');
    const navBar = document.getElementById('navBar');

    if (navBarText.className === 'DarkLogoText') {
      navBarText.className = 'LightLogoText';
      navBar.className = 'LightNavBar';
    } else {
      navBarText.className = 'DarkLogoText';
      navBar.className = 'DarkNavBar';
    }
  }

  consoleLogger():void{
    console.log('clicked');
  }

  newTab(): void {
    window.open('https://github.com/MetaBrian/AngularSNAP', '_blank');
  }
}
<div id="navBar" class="DarkNavBar">
  <div class="Logo">
    <img
      id="LogoImage"
      src="../assets/Thanos_Snap.png"
      alt="Thanos Snapping Emote"
    />
    <button id="LogoButton" class='darkButton'(click)="consoleLogger()">
      <!-- should be a link to the home page on click -->
      <h1 class="DarkLogoText" id="LogoText">AngularSNAP!</h1>
    </button>
  </div>

  <button class="Links">Deck Builder</button>
  <button class="Links">Card Rater</button>
  <button (click)="newTab()" class="externalLinks">GitHub</button>
  <button (click)="modeSwitch()" class="externalLinks">
    <img height="100%" src="../assets/lightdark.png" alt="Sun & Moon symbol" />
  </button>
</div>
